mapper.sql:
select op.ID, op.ORDER_ID, op.ORDER_TOTAL_PRICE, op.PAY_TIME, op.USER_PHONE, op.DEALERS_ID, op.COMFIRM, op.COMFIRM_DATE, op.RESC, op.ITEM_ID, op.ITEM_NAME, op.PRICE, op.QUANTITY, op.TOTAL_PRICE, case when comfirm = '0' then op.PRICE*p.discount else op.DISCOUNT_PRICE end as DISCOUNT_PRICE, case when comfirm = '0' then op.PRICE*p.discount*op.QUANTITY else op.DISCOUNT_TOTAL_PRICE end as DISCOUNT_TOTAL_PRICE, case when comfirm = '0' then op.TOTAL_PRICE - op.PRICE*p.discount*op.QUANTITY else op.REBATE end as REBATE, op.SKU_ID, op.SKU_TITLE, d.name as dealerName from order_product op LEFT JOIN product p on op.ITEM_ID = p.PRODUCT_ID and op.SKU_ID = p.SKU_ID LEFT JOIN dealers d on op.USER_PHONE = d.PHONE
and op.COMFIRM = #{comfirm,jdbcType=VARCHAR}
and op.USER_PHONE = #{userPhone,jdbcType=VARCHAR}
and op.ORDER_ID = #{orderId,jdbcType=VARCHAR}
and op.PAY_TIME >= #{payTimeBegin,jdbcType=VARCHAR}
and op.PAY_TIME <= #{payTimeEnd,jdbcType=VARCHAR}
order by op.ID asc
